import React from 'react'
import chart from "./assets/images/chart.png";
import bitcoin from "./assets/images/bitcoin.png";
import ethereum from "./assets/images/eth.png";
import bitcoinCash from "./assets/images/green.png";
import litecoin from "./assets/images/lit.png";
import "./table.css";

const Lists = [
    {
      icon: bitcoin,
      name: "Bitcoin",
      code: "BTC",
      price: "GHS 314,259.22",
      change: -41.86,
      chart: chart,
    },
    {
      icon: ethereum,
      name: "Ethereum",
      code: "ETH",
      price: "GHS 314,654.22",
      change: -89.75,
      chart: chart,
    },
    {
      icon: bitcoinCash,
      name: "Litecoin",
      code: "LTC",
      price: "GHS 894,479.22",
      change: -59.23,
      chart: chart,
    },
    {
      icon: litecoin,
      name: "Bitcoin Cash",
      code: "BCH",
      price: "GHS 6534,259.22",
      change: -44.15,
      chart: chart,
    },
  ];


  const Table = () => {
    return (
      <div className="main__table">
        <div className="table__wrapper">
          <table style={{ width: "100%" }}>
            <thead>
              <tr>
                <th className="index">#</th>
                <th>Name</th>
                <th>Price</th>
                <th>Change</th>
                <th>Chart</th>
                <th>Trade</th>
              </tr>
            </thead>
            <tbody>
              {Lists.map((Lists, index) => {
                return (
                  <tr>
                    <td className="index">{index + 1}</td>
                    <td className="table__name">
                      <img src={Lists.icon} alt="logo" />
                      <h4 class="name">{Lists.name}</h4>
                      <h4 className="code">{Lists.code}</h4>
                    </td>
                    <td className="price">{Lists.price}</td>
                    <td className="change">{Lists.change}</td>
                    <td>
                      <img src={Lists.chart} alt="chart logo" />
                    </td>
                    <td>
                      <button class="trade__btn">Buy</button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  export default Table;
